import React, {useState} from 'react';

function AddReview  () {
    const [newRating, setNewRating] = useState([
        {
        dish_name: "",
        cuisine: "",
        dish_size: 0,
        score: 1
        }
    ])
    const [reviewData, setReviewData] = useState({
        restaurant_name: "",
        ratings: [...newRating]
    });

    function handleReviewInputChange(event){
        const {name, value} = event.target;

        const newDishRating = {...reviewData.ratings}


        setReviewData({...reviewData,
        [name]: value})
        console.log(reviewData)

    }

    const handleSubmit = (event) => {
        event.preventDefault()
    }

    return(
        <div>
            <h2>Write a Review</h2>
            <form onSubmit={handleSubmit}>
                <label>Restaurant: 
                    <input
                    type='text'
                    name='restaurant_name'
                    value={reviewData.restaurant_name}
                    onChange={handleReviewInputChange}
                    />
                </label>
            <br></br>
                <label>
                    Name of Dish: 
                    <input
                    type="text"
                    name="dish_name"
                    value={reviewData.dish_name}
                    onChange={handleReviewInputChange}
                    />
                </label>
            <br></br>
                <label>
                    Cuisine Type:
                    <input
                    type="text"
                    name="cuisine"
                    value={reviewData.cuisine}
                    onChange={handleReviewInputChange}
                    />
                </label>
            {/* <br></br>
                <label>
                    Reason for visit:
                    <input
                    type="text"
                    name="companions"
                    value={reviewData.companions}
                    onChange={handleReviewInputChange}
                    />
                </label> */}
            <br></br>
                <label>
                    Size of Dish: 
                    <input
                    placeholder='Scale of 0-3'
                    type="number"
                    name="dishSizeRating"
                    value={reviewData.dishSizeRating}
                    onChange={handleReviewInputChange}
                    />
                </label>
            <br></br>
                <label>
                    Rank the Dish:
                    <input
                    placeholder='Scale of 1 - 9'
                    type="number"
                    name="dishRanking"
                    value={reviewData.dishRanking}
                    onChange={handleReviewInputChange}
                    />
                </label>
            <br></br>
                <button type="submit">Add Review</button>
            </form>
        </div>
    )
}

export default AddReview
