import React, {useState} from 'react';

function AddReview  () {

    const [reviewData, setReviewData] = useState({
        dishName: '',
        restaurant: '',
        address: '',
        cuisineGenre: '',
        companions: '',
        dishSizeRating: 0,
        dishRanking: 1,
    });

    function handleReviewInputChange(event){
        const {name, value} = event.target;
        setReviewData({...reviewData,
        [name]: value})
        console.log(reviewData)

    }
    // const handleInputChange = (event) => {
    //     const {name, value } = event.target;
    //     setReviewData({... reviewData, [name]: value})
    // };

    // const handleRankingChange = (event) => {
    //     const value = parseInt(event.target.value);
    //     setReviewData({...reviewData, dishranking: value})
    // };

    // const handleSizeRatingChange = (event) => {
    //     const value = parseInt(event.target.value);
    //     setReviewData({...reviewData, dishSizeRating: value})
    // }

    const handleSubmit = (event) => {
        event.preventDefault()
    }

    return(
        <div>
            <h2>Write a Review</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Name of dish:
                    <input
                    type="text"
                    name="dishName"
                    value={reviewData.dishName}
                    onChange={handleReviewInputChange}
                    />
                </label>
            <br></br>
                <label>
                    Genre of Cuisine:
                    <input
                    type="text"
                    name="cuisineGenre"
                    value={reviewData.cuisineGenre}
                    onChange={handleReviewInputChange}
                    />
                </label>
            <br></br>
                <label>
                    Reason for visit:
                    <input
                    type="text"
                    name="companions"
                    value={reviewData.companions}
                    onChange={handleReviewInputChange}
                    />
                </label>
            <br></br>
                <label>
                    Size of Dish (0-3 scale);
                    <input
                    type="number"
                    name="dishSizeRating"
                    value={reviewData.dishSizeRating}
                    onChange={handleReviewInputChange}
                    />
                </label>
            <br></br>
                <label>
                    Rank the Dish (1-9 scale):
                    <input
                    type="number"
                    name="dishRanking"
                    value={reviewData.dishRanking}
                    onChange={handleReviewInputChange}
                    />
                </label>
            <br></br>
                <button type="submit">Submit Review</button>
            </form>
        </div>
    )
}

export default AddReview
