import {useState, useEffect} from 'react';

function Profile (){
    const [profiles, setProfiles] = useState([])

    useEffect(() => {
        fetch(`http://localhost:3000/profiles`)
        .then(response => response.json())
        .then(returnedData => {
            setProfiles(returnedData)
        })
    }, [])

    const dinerProfile = profiles.map((profile) => {
        const {id, firstname, lastname, dishes_rated, reviews} = profile;
        // console.log(profile)
            const eachRestaurantRating = reviews.map((review, index) => {

                const {restaurant_name, ratings} = review

                    const dishRatings = ratings.map((rating, index)=> {

                        const {dish_name, cuisine, dish_size, score} = rating;
                        return (
                            <li key={index}>
                                <p>{dish_name}</p>
                                <p>{cuisine}</p>
                                <p>{dish_size}</p>
                                <p>{score}</p>
                            </li>
                        )
                    })
                    return (
                        <ul>{restaurant_name}
                            {dishRatings}
                        </ul>
                    )
            })

            return (
                <span className="dinerCard" key={id}>
                    <p>{firstname} {lastname}</p>
                    <p>Dishes Rated: {Number(dishes_rated)}</p>
                    <ul>{eachRestaurantRating}</ul>
                </span>
            )

    })

    return (
        <div className="profiles">
            {dinerProfile}
        </div>
    )
}

export default Profile
