import Card from 'react-bootstrap/Card';
import { useOutletContext } from 'react-router-dom';

function InstagramCard({}) {
    // console.log(instagram)
    // const {name, account, link, followers, description} = instagram;
    const [data] = useOutletContext();

    const itemArray = data.map((item, index)=> {
        const cards = item.instagram.map((instagram, index)=> {
            const {name, followers, description, link, account} = instagram;
            return (
                <Card key={index} style={{ width: '18rem' }}>
                    <Card.Body>
                        <Card.Title>{name} </Card.Title>
                        <Card.Text>{followers}</Card.Text>
                        <Card.Text>{description}</Card.Text>
                        <Card.Link href={link}>{account}</Card.Link>
                    </Card.Body>
                </Card>
            )
        })
        return (
            <div key={index}>
                {cards}
            </div>
        )
    }) 
    return (
        {itemArray}
    )
}

export default InstagramCard;
