import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function AddProfile({ userData, setUserData }){
    const [newProfile, setNewProfile] = useState({
        reviews: []
    });

    const navigate = useNavigate();

    function handleNewProfileInput(event){
        const {value, name} = event.target;

        setNewProfile({...newProfile,
            [name] : value
        })
    }

    function handleNewProfileSubmit(event){
        event.preventDefault();

        fetch(`http://localhost:3000/profiles`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json"
            },
            body: JSON.stringify(newProfile)
        })
        .then(response => {
            if (response.ok) {
                return response.json().then((returnedData) => {
                    setUserData([...userData, returnedData])
                    navigate("/profiles")
                })
            }
        })

    }

    const inputStyling = {
        width: '80%',
        marginLeft: '5vh',
        marginTop: '5vh',
        padding: '2vh',
        borderRadius: '5px',
        border: '1px solid #8B4513', // Dark tan outline color
        boxSizing: 'border-box',
        fontSize: '16px',
        color: 'black',
        outline: 'none', // Remove blue outline on hover
        backgroundColor: '#FFE4C4',
    }


    return (
        <>
            <h3>Create New Profile</h3>
            <form 
            className='form-submit'
            onSubmit={handleNewProfileSubmit}
            >
                <input 
                style={inputStyling}
                type="text"
                name="username"
                placeholder="Username"
                onChange={handleNewProfileInput}
                ></input>
                <input 
                style={inputStyling}
                type="text" 
                name="firstname"
                placeholder="First Name"
                onChange={handleNewProfileInput}
                ></input>
                <input 
                style={inputStyling}
                type="text" 
                name="lastname"
                placeholder="Last Name"
                onChange={handleNewProfileInput}
                ></input>
                <input 
                style={inputStyling}
                type="text" 
                name="location"
                placeholder="City, State"
                onChange={handleNewProfileInput}
                ></input>
                <button type="submit">Create</button>
            </form>
        </>
    )
}

export default AddProfile
