import {useState} from 'react';
import { useParams } from 'react-router-dom';

function AddReview  ({ userData }) {
    let restaurantExists;
    const {profileID} = useParams();

    const [reviewData, setReviewData] = useState({
        restaurant_name: "",
        ratings: []
    });

    function handleReviewInputChange(event){
        const {name, value} = event.target;
        const newDishRating = [{
            ...reviewData.ratings,
            [name] : value
        }]

        setReviewData({
            ...reviewData,
            ratings: newDishRating
            })

        if( name === "dish_name" || name === 'cuisine') {
            // const newDishRating = {
            //     ...reviewData.ratings,
            //     [name] : value
            // }
            setReviewData({
                ...reviewData,
                ratings: [
                    ...reviewData.ratings, 
                    {[name] : value}
                    ]
                })
        } else if (
            name === "dish_size" ||
            name === "score"
        ) {
            // const newNumberRating = {
            //     ...reviewData.ratings,
            //     [name] : Number(value)
            // }
            setReviewData({
                ...reviewData,
                ratings: [...reviewData.ratings, {
                    [name] : Number(value)
                }]
            })
        } else {
            setReviewData({
                ...reviewData,
                [name] : value
            })
        }

        userData.filter((user)=> {
            if (name === "restaurant_name") {
                if (user.reviews.some(item => item.restaurant_name === value)) {
                    return restaurantExists = true;
                } else {
                return restaurantExists = false
                }
            }
        })

    }

   function handleReviewSubmit(event){
        event.preventDefault()


        userData.filter((user)=> {
            if (user.id === profileID) {
                fetch (`http://localhost:3000/profiles/${user.id}`, {
                    method: "PATCH",
                    headers: {
                        "Content-Type": "application/json",
                        "Accept": "application/json"
                    },
                    body: JSON.stringify({
                        ...user,
                        reviews: [
                            ...user.reviews,
                            reviewData,
                            // restaurantExists ? { post to ratings } : { post to reviews w/ restaurant name}
                            ]
                        })
                })
                .then(response => {
                    if (response.ok) {
                        return response.json().then(returnedData => {
                            console.log(returnedData)
                        })
                    }
                })
            }


        })
    }

    return(
        <div>
            <h2>Write a Review</h2>
            <form onSubmit={handleReviewSubmit}>
                <label>Restaurant: 
                    <input
                    type='text'
                    name='restaurant_name'
                    value={reviewData.restaurant_name}
                    onChange={handleReviewInputChange}
                    />
                </label>
            <br></br>
                <label>
                    Name of Dish: 
                    <input
                    type="text"
                    name="dish_name"
                    value={reviewData.dish_name}
                    onChange={handleReviewInputChange}
                    />
                </label>
            <br></br>
                <label>
                    Cuisine Type:
                    <input
                    type="text"
                    name="cuisine"
                    value={reviewData.cuisine}
                    onChange={handleReviewInputChange}
                    />
                </label>
            {/* <br></br>
                <label>
                    Reason for visit:
                    <input
                    type="text"
                    name="companions"
                    value={reviewData.companions}
                    onChange={handleReviewInputChange}
                    />
                </label> */}
            <br></br>
                <label>
                    Size of Dish: 
                    <input
                    placeholder='Scale of 0-3'
                    type="number"
                    name="dish_size"
                    value={reviewData.dishSizeRating}
                    onChange={handleReviewInputChange}
                    />
                </label>
            <br></br>
                <label>
                    Rank the Dish:
                    <input
                    placeholder='Scale of 1 - 9'
                    type="number"
                    name="score"
                    value={reviewData.dishRanking}
                    onChange={handleReviewInputChange}
                    />
                </label>
            <br></br>
                <button type="submit">Add Review</button>
            </form>
        </div>
    )
}

export default AddReview
